% Fourier transformclearecho ondf=0.01; %frequency resolutionfs=10; %sampling frequencyts=1/fs; %sampling periodT1=-5; T2=5; %observation time interval (from T1 to T2 sec)t=[T1:ts:T2]; %observation time vector% Signal genaration of rectangular pulsetau=2; % Pulse width [sec]x1=rect(tau, T1, T2, fs, df);[X1,x11,df1]=fft_mod(x1,ts,df);X11=X1/fs; %scalingf=[0:df1:df1*(length(x11)-1)]-fs/2; %frequency vector (range to plot)plot(t,x1); axis([T1 T2 -2 4])title('Waveform of rectangular pulse');pause %Press any key to see magnitude spectrummag_X11=fftshift(abs(X11));plot(f,mag_X11)title('Magnitude spectrum of rectangular pulse');pause %%Press any key to see spectrum in dB scalemag_X11_db=20*LOG10(mag_X11);plot(f,mag_X11_db); axis([-5 5 -60 10])title('Energy spectrum of rectangular pulse (dB scale)');pause %%Press any key to continue% Signal generation of triangular pulsex2=triangle(tau, T1, T2, fs, df);[X2,x21,df1]=fft_mod(x2,ts,df);X21=X2/fs;f=[0:df1:df1*(length(x21)-1)]-fs/2;figureplot(t,x2); axis([T1 T2 -2 4])title('Waveform of triangular pulse');pause %Press any key to see magnitude spectrummag_X21=fftshift(abs(X21));plot(f,mag_X21)title('Magnitude spectrum of triangular pulse');pause %%Press any key to see spectrum in dB scalemag_X21_db=20*LOG10(mag_X21);plot(f,mag_X21_db); axis([-5 5 -60 10])title('Energy spectrum of triangular pulse (dB scale)');